        function scrollJkTab(selector, event) {
            var target = $(selector + '.scroll-holder')[0];
            var ul = target.getElementsByTagName('*')[0];
            var ul_data = ul.getBoundingClientRect();
            var ul_right = 1;
            var target_data = target.getBoundingClientRect();
            if (ul_data.width > Math.round(target_data.width)) {
                var evt = window.event || event; //equalize event object
                var delta = evt.detail ? evt.detail * (-120) : evt.wheelDelta; //check for detail first so Opera uses that instead of wheelDelta
                var offset = 15;
                var direction = delta < 0 ? 1 : -1;
                var currentPos = target.scrollLeft;
                target.scrollLeft = currentPos + offset * direction;
                if (evt.preventDefault) {
                    evt.preventDefault();
                } else {
                    return false
                }
            }
        }

        function scrollJkTabPos(selector) {
            var parent = $(selector).parent()[0];
            if (parent) {
                var target = document.querySelector(selector + '.scroll-holder');
                var liactive = document.querySelector(selector + ' li.active');
                if (liactive === undefined) {
                    liactive = document.querySelector(selector + ' .scroll-holder');
                }
                if (liactive.offsetLeft > parent.offsetWidth || liactive.offsetLeft < parent.offsetLeft) {
                     target.scrollLeft = liactive.offsetLeft;
                    //if (liactive.offsetLeft > parent.offsetWidth / 2) {
                        //target.scrollLeft = parent.offsetWidth - liactive.offsetWidth;
                    //}
                }
            }
        }

        var mousewheelevt = (/Firefox/i.test(navigator.userAgent)) ? "DOMMouseScroll" : "mousewheel"; //FF doesn't recognize mousewheel as of FF3.x

        /**
         * járműkategória
         */

         if ($('').length > 0) {
            scrollJkTabPos('');
            if ($('')[0].attachEvent) { //if IE (and Opera depending on user setting)
                $('.tab-holder')[0].attachEvent("on" + mousewheelevt, scrollJkTab.bind(this, '.tab-holder'));
            } else if ($('')[0].addEventListener) { //WC3 browsers
                $('.tab-holder')[0].addEventListener(mousewheelevt, scrollJkTab.bind(this, '.tab-holder'), false)
            }
        }

